import { useState } from 'react';
import './App.css';

function App() {
  const [taskname, settaskname] = useState('');
  const [todotasklist, settodotasklist] = useState('');
  const [donetasklist, setdonetasklist] = useState('');

  const onChangeInputArea = (event) => settaskname(event.target.value);

  const onChangeAddtaskButton = () => {
    const newtodotasklist = [...todotasklist, taskname];
    settodotasklist(newtodotasklist);
    settaskname('');
  }

  const onChangeCompleteTaskButton = (index) => {
    const newtodotasklist = [...todotasklist];
    newtodotasklist.splice(index, 1);
    settodotasklist(newtodotasklist);

    const newdonetasklist = [...donetasklist, todotasklist[index]];
    setdonetasklist(newdonetasklist);
  }

  return (
    <div>
      <div className='input-area'>
        <p>タスクを追加</p>
        <input placeholder='タスク名を入力' value={taskname} onChange={onChangeInputArea}/>
        <button onClick={onChangeAddtaskButton}>追加</button>
      </div>
      <div className='todo-list'>
        <p>ToDoリスト</p>
        {todotasklist && todotasklist.map((todotask, index) => {
          return (
            <div key={index}>
              <p>{todotask}</p>
              <button onClick={() => onChangeCompleteTaskButton({index})}>完了</button>
            </div>
          );
        })}
      </div>
      <div className='done-list'>
        <p>Doneリスト</p>
        {todotasklist && todotasklist.map((todotask, index) => {
          return (
            <div key={index}>
              <p>{todotask}</p>
              <button onClick={() => onChangeCompleteTaskButton({index})}>完了</button>
            </div>
          );
        })}
      </div>
    </div>

  );
}

export default App;
