import { useState } from 'react';
import './App.css';

function App() {
  const [taskname, settaskname] = useState('');
  const [todotasklist, setincompletetasklist] = useState('');

  const onChangeInputArea = (event) => settaskname(event.target.value);

  const onChangeAddtaskButton = ()=> {
    const newtodotasklist = [...todotasklist, taskname];
    setincompletetasklist(newtodotasklist);
    console.log(todotasklist);
  }

  return (
    <div>
      <div className='input-area'>
        <p>タスクを追加</p>
        <input placeholder='タスク名を入力' value={taskname} onChange={onChangeInputArea}/>
        <button onClick={onChangeAddtaskButton}>追加</button>
      </div>
      <div className='todo-list'>
        <p>ToDoリスト</p>
        {todotasklist.map((todotask) => {
            return (
              <div key={todotask}>
                <p>{todotask}</p>
                <button>完了</button>
              </div>
            );
          })}
      </div>
      <div className='done-list'>
        <p>Doneリスト</p>
        <div>
          <p>タスク2</p>
          <button>戻す</button>
        </div>
      </div>
    </div>

  );
}

export default App;
